import React, { useState } from 'react';
import inventory from '../Inventory';

function Filter() {
    const [filters, setFilters] = useState({
        price: '',
        year: '',
        type: '',
        sleeps: '',
        floorPlan: '',
        weight: '',
        length: '',
        toyHaulerGarageLength: '',
        slideOuts: ''
    });

    const handleChange = (e) => {
        setFilters({
            ...filters,
            [e.target.name]: e.target.value
        });
    };

    const filteredInventory = inventory.filter(item => {
        return Object.keys(filters).every(key => 
            !filters[key] || item[key].toString().toLowerCase().includes(filters[key].toString().toLowerCase())
        );
    });

    return (
        <div>
            <div>
                <input name="price" placeholder="Price" value={filters.price} onChange={handleChange} />
                <input name="year" placeholder="Year" value={filters.year} onChange={handleChange} />
                <input name="type" placeholder="RV Class" value={filters.type} onChange={handleChange} />
                <input name="sleeps" placeholder="Sleeps" value={filters.sleeps} onChange={handleChange} />
                <input name="floorPlan" placeholder="Floor Plan" value={filters.floorPlan} onChange={handleChange} />
                <input name="weight" placeholder="Weight" value={filters.weight} onChange={handleChange} />
                <input name="length" placeholder="Length" value={filters.length} onChange={handleChange} />
                <input name="toyHaulerGarageLength" placeholder="Toyhauler Garage Length" value={filters.toyHaulerGarageLength} onChange={handleChange} />
                <input name="slideOuts" placeholder="Slide Outs" value={filters.slideOuts} onChange={handleChange} />
            </div>
            <div>
                {filteredInventory.map(rv => (
                    <div key={rv.id}>
                        <h2>{rv.name}</h2>
                        <p>Price: ${rv.price}</p>
                        <p>Year: {rv.year}</p>
                        <p>Type: {rv.type}</p>
                        <p>Sleeps: {rv.sleeps}</p>
                        <p>Floor Plan: {rv.floorPlan}</p>
                        <p>Weight: {rv.weight} lbs</p>
                        <p>Length: {rv.length} ft</p>
                        <p>Toyhauler Garage Length: {rv.toyHaulerGarageLength} ft</p>
                        <p>Slide Outs: {rv.slideOuts}</p>
                        <img src={rv.image} alt={rv.name} />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Filter;
