import React from 'react'
import { useScreenWidth } from '../Global/ScreenWidthContext'

function Hero() {

  const isMobile = useScreenWidth()

  return (
    <div className="hero__container h-screen">
      {isMobile ? (
        <>
          <div className="flex bg-cover h-screen bg-lively-orange bg-center text-white text-center p-12">
              <h3 className="font-bold mb-3">Adventure Starts Here:</h3> 
              <h1 className='text-4xl'>Experience the Freedom of the Open Road</h1>
              <img src="/images/hero/test.png" alt="bumper-pull" />
              <button className="bg-orange-500 hover:bg-orange-600 text-white font-bold py-2 px-4 rounded">
                  Browse Our Collection
              </button>
          </div>
        </>
      ) : (
        <>
        DES
        </>
      )}
    </div>

  )
}

export default Hero
