import React from 'react'
import { useScreenWidth } from '../Global/ScreenWidthContext'

function Hero() {

  const isMobile = useScreenWidth()

  return (
    <div class="bg-cover bg-center text-white text-center p-12">
        <h1 class="text-4xl font-bold mb-3">Adventure Starts Here: Experience the Freedom of the Open Road</h1>
        <p class="text-xl mb-5">Ready to break free from the everyday? Our selection of premium orange motorhomes is your ticket to unforgettable journeys.</p>
        <button class="bg-orange-500 hover:bg-orange-600 text-white font-bold py-2 px-4 rounded">
            Browse Our Collection
        </button>
    </div>

  )
}

export default Hero
