import React from 'react'
import { useScreenWidth } from './ScreenWidthContext'

// MUI ICONS 
import LocalPhoneIcon from '@mui/icons-material/LocalPhone';
import { useNavLinks } from './NavContext';

function Footer() {

  const isMobile = useScreenWidth()

  const { navLinks } = useNavLinks()

  return (
    <div className='footer__container bg-lively-orange text-white'>
      {isMobile ? (
        <div className='mobile__footer flex flex-col'>
          <div className="footer_logo">
            <h2>The Orange RV</h2>
          </div>

            <div className="footer__phone">
              <LocalPhoneIcon />
              <a href="#">+1 (888) 888-8888</a>
            </div>


          <div className="footer__navlinks">
            <ul>
              {navLinks.map((item, index) => (
                <li>
                  <Link className={item.className} to={item.href}>
                  {item.linkName}
                  </Link>
                </li>
              ))}
            </ul>
          </div>
        </div>
      ) : (
        <>
        </>
      )}
    </div>
  )
}

export default Footer
